using Amazon.CDK;
using Amazon.CDK.AWS.CloudWatch;


var fn = new Function(this, "MyFunction", new FunctionProps {
    Runtime = Runtime.NODEJS_16_X,
    Handler = "index.handler",
    Code = Code.FromAsset(Join(__dirname, "lambda-handler")),
    Timeout = Duration.Minutes(5)
});

if (fn.Timeout)
{
    new Alarm(this, "MyAlarm", new AlarmProps {
        Metric = fn.MetricDuration().With(new MetricOptions {
            Statistic = "Maximum"
        }),
        EvaluationPeriods = 1,
        DatapointsToAlarm = 1,
        Threshold = fn.Timeout.ToMilliseconds(),
        TreatMissingData = TreatMissingData.IGNORE,
        AlarmName = "My Lambda Timeout"
    });
}

java.source:
import software.amazon.awscdk.core.*;
import software.amazon.awscdk.services.cloudwatch.*;


Function fn = Function.Builder.create(this, "MyFunction")
        .runtime(Runtime.NODEJS_16_X)
        .handler("index.handler")
        .code(Code.fromAsset(join(__dirname, "lambda-handler")))
        .timeout(Duration.minutes(5))
        .build();

if (fn.getTimeout()) {
    Alarm.Builder.create(this, "MyAlarm")
            .metric(fn.metricDuration().with(MetricOptions.builder()
                    .statistic("Maximum")
                    .build()))
            .evaluationPeriods(1)
            .datapointsToAlarm(1)
            .threshold(fn.timeout.toMilliseconds())
            .treatMissingData(TreatMissingData.IGNORE)
            .alarmName("My Lambda Timeout")
            .build();
}

go.source:
import cdk "github.com/aws-samples/dummy/awscdkcore"
import "github.com/aws-samples/dummy/awscdkawscloudwatch"


fn := lambda.NewFunction(this, jsii.String("MyFunction"), &FunctionProps{
	Runtime: lambda.Runtime_NODEJS_16_X(),
	Handler: jsii.String("index.handler"),
	Code: lambda.Code_FromAsset(path.join(__dirname, jsii.String("lambda-handler"))),
	Timeout: cdk.Duration_Minutes(jsii.Number(5)),
})

if fn.Timeout {
	cloudwatch.NewAlarm(this, jsii.String("MyAlarm"), &AlarmProps{
		Metric: fn.metricDuration().With(&MetricOptions{
			Statistic: jsii.String("Maximum"),
		}),
		EvaluationPeriods: jsii.Number(1),
		DatapointsToAlarm: jsii.Number(1),
		Threshold: fn.*Timeout.ToMilliseconds(),
		TreatMissingData: cloudwatch.TreatMissingData_IGNORE,
		AlarmName: jsii.String("My Lambda Timeout"),
	})
}

